import React, { useState, useCallback, useMemo, useEffect } from 'react';
import WelcomeScreen from './components/WelcomeScreen';
import QuizScreen from './components/QuizScreen';
import ResultsScreen from './components/ResultsScreen';
import { quizData } from './constants/quizData';
import type { GameState, QuizQuestion } from './types';

// Base64 encoded audio for whistle sounds
const singleWhistleSound = 'data:audio/mpeg;base64,-EAAAAAQkAMQAAAAAAAARkWg==';
const tripleWhistleSound = 'data:audio/mpeg;base64,----';

function App() {
  const [gameState, setGameState] = useState<GameState>('welcome');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [activeQuestions, setActiveQuestions] = useState<QuizQuestion[]>([]);

  const singleWhistle = useMemo(() => new Audio(singleWhistleSound), []);
  const tripleWhistle = useMemo(() => new Audio(tripleWhistleSound), []);

  useEffect(() => {
    if (gameState === 'results') {
      tripleWhistle.play().catch(e => console.error("Error playing triple whistle sound:", e));
    }
  }, [gameState, tripleWhistle]);

  const startFullQuiz = useCallback(() => {
    setActiveQuestions(quizData);
    setCurrentQuestionIndex(0);
    setScore(0);
    setGameState('playing');
    singleWhistle.play().catch(e => console.error("Error playing single whistle sound:", e));
  }, [singleWhistle]);

  const startPracticeQuiz = useCallback((law: string) => {
    const practiceQuestions = quizData.filter(q => q.law === law);
    setActiveQuestions(practiceQuestions);
    setCurrentQuestionIndex(0);
    setScore(0);
    setGameState('playing');
    singleWhistle.play().catch(e => console.error("Error playing single whistle sound:", e));
  }, [singleWhistle]);


  const handleAnswer = useCallback((isCorrect: boolean) => {
    if (isCorrect) {
      setScore(prevScore => prevScore + 1);
    }
  }, []);

  const handleNextQuestion = useCallback(() => {
    if (currentQuestionIndex < activeQuestions.length - 1) {
      const nextIndex = currentQuestionIndex + 1;
      // Check if the law of the next question is different from the current one
      if (activeQuestions[currentQuestionIndex].law !== activeQuestions[nextIndex].law) {
         singleWhistle.play().catch(e => console.error("Error playing single whistle sound:", e));
      }
      setCurrentQuestionIndex(nextIndex);
    } else {
      setGameState('results');
    }
  }, [currentQuestionIndex, activeQuestions, singleWhistle]);

  const restartQuiz = useCallback(() => {
    setGameState('welcome');
    setActiveQuestions([]);
    setCurrentQuestionIndex(0);
    setScore(0);
  }, []);

  const renderContent = () => {
    switch (gameState) {
      case 'playing':
        return (
          <QuizScreen
            questionData={activeQuestions[currentQuestionIndex]}
            questionNumber={currentQuestionIndex + 1}
            totalQuestions={activeQuestions.length}
            onAnswer={handleAnswer}
            onNext={handleNextQuestion}
          />
        );
      case 'results':
        return (
          <ResultsScreen
            score={score}
            totalQuestions={activeQuestions.length}
            onRestart={restartQuiz}
          />
        );
      case 'welcome':
      default:
        return <WelcomeScreen onStartFullQuiz={startFullQuiz} onStartPractice={startPracticeQuiz} />;
    }
  };

  return (
    <div className="bg-gray-900 text-white min-h-screen flex items-center justify-center p-4 font-sans">
      <div className="w-full max-w-3xl">
        {renderContent()}
      </div>
    </div>
  );
}

export default App;
